import React, { useEffect, useState, useRef } from 'react';
import '../styles/login.css';
import ProfilePic from '../styles/MyImage.jpeg';

const MainPage = () => {
    const [typedName, setTypedName] = useState('');
    const fullName = "  I AM MUHAMMAD MEHDI CHANGEZI";
    const hasTyped = useRef(false);
    const phrases = [
        'Student',
        'Frontend developer',
        'Backend developer',
        'Mobile app developer',
        'AI Enthusiast',
        'gym boy'
    ];

    const [typedPhrase, setTypedPhrase] = useState('');
    const [phraseIndex, setPhraseIndex] = useState(0);
    const [isDeleting, setIsDeleting] = useState(false);

    useEffect(() => {
        let index = 0;
        const currentPhrase = phrases[phraseIndex];

        const typingInterval = setInterval(() => {
            if (isDeleting) {
                // Remove character
                setTypedPhrase((prev) => prev.slice(0, prev.length - 1));
                if (typedPhrase.length === 0) {
                    // Switch to the next phrase
                    setIsDeleting(false);
                    setPhraseIndex((prev) => (prev + 1) % phrases.length); // Cycle through phrases
                }
            } else {
                // Add character
                setTypedPhrase((prev) => currentPhrase.slice(0, index + 1));
                if (index < currentPhrase.length - 1) {
                    index++;
                } else {
                    setIsDeleting(true); // Start deleting after completing the current phrase
                }
            }
        }, isDeleting ? 50 : 100); // Adjust typing speed

        return () => clearInterval(typingInterval); // Cleanup on unmount
    }, [isDeleting, phraseIndex, typedPhrase]);
  

    useEffect(() => {
        let index = 0;

        const typingInterval = setInterval(() => {
            if (index < fullName.length) { // Ensure we are within bounds
                setTypedName((prev) => prev + fullName.charAt(index)); // Append the next character
                index++;
            } else {
                clearInterval(typingInterval); // Clear the interval when done
            }
        }, 100); // Adjust typing speed by changing this value (milliseconds)

        return () => clearInterval(typingInterval); // Cleanup interval on component unmount
    }, []); // Empty dependency array to run on component mount

    return (
        <>
            <div className="header-about-container">
                <header>
                    <h1>{typedName}</h1>
                    <nav>
                        <ul>
                            <li><a href="#about">About</a></li>
                            <li><a href="#projects">Projects</a></li>
                            <li><a href="#skills">Skills</a></li>
                            <li><a href="#experience">Experience</a></li>
                            <li><a href="#awards">Awards</a></li>
                            <li><a href="#contact">Contact</a></li>
                        </ul>
                    </nav>
                </header>
                
                <section id="about" className="about-section">
    <h2>About Me</h2>
    <div className="profile-pic-container">
        <img src={ProfilePic} alt="My Profile" className="profile-pic" />
    </div>
    <>
            <div className="about-section">
                <p style={{ textAlign: 'center', color: 'white', fontWeight: 'bold' }}>
                    <span style={{ fontWeight: 'bold', fontSize: '1.2em', color: 'white' }}>Hello!</span> My name is Mehdi, and I am a{' '}
                    <span style={{ fontWeight: 'bold', color: 'white', fontSize: '1.2em' }}>{typedPhrase}</span>.
                </p>
                {/* You can continue adding more sections below as needed */}
            </div>
        </>

</section>

            </div>

            <section id="projects" className="projects-section">
                <div className="container">
                    <h2>Projects</h2>
                    <div className="projects-grid">
                        <div className="project-card">
                            <h3>Project 1</h3>
                            <p>Short description of the project goes here. Explain what technology you used and the features implemented.</p>
                        </div>
                        <div className="project-card">
                            <h3>Project 2</h3>
                            <p>Short description of the project goes here. Explain what technology you used and the features implemented.</p>
                        </div>
                        <div className="project-card">
                            <h3>Project 3</h3>
                            <p>Short description of the project goes here. Explain what technology you used and the features implemented.</p>
                        </div>
                    </div>
                </div>
            </section>

            <section id="skills" className="skills-section">
                <div className="container">
